mapper.select:
SELECT pg.id id, pg.name name from p_product_property pp INNER JOIN p_props_group pg on pp.group_id=pg.id
and pp.product_code=#{productCode}
group by pp.group_id

SELECT pps.id id, pps.name name from p_product_property pp inner join p_props pps on pps.id=pp.property_id
and pp.product_code=#{productCode}
and pp.group_id=#{groupId}
